//-------- example -----------------------

var ping = require('../index');

var hosts = ['206.130.99.2', 'google.com', 'yahoo.com'];
hosts.forEach(function(host){
    ping.sys.probe(host, function(result){
        var msg = result.isAlive ? 'host ' + result.host + ' is alive' : 'host ' + result.host + ' is dead';
        var msg2 = 'Code: ' + result.code;
        var msg3 = 'Min: ' + result.stats.min + ', Average: ' + result.stats.avg + ', Max: ' + result.stats.max + ', Standard Deviation: ' + result.stats.stddev;
        var msg4 = 'Actual Ping Data: ' + result.output;

        console.log('-------- PING ACTIVATED ----------');
        console.log(msg);
        console.log(msg2);
        console.log(msg3);
        console.log(msg4);

    });
});

